PREFIX dct: <http://purl.org/dc/terms/>
PREFIX prov: <http://www.w3.org/ns/prov#>
PREFIX eli: <http://data.europa.eu/eli/ontology#>
PREFIX besluit: <http://data.vlaanderen.be/ns/besluit#>
PREFIX oa: <http://www.w3.org/ns/oa#>
SELECT ?annotation ?target ?title ?motivering (GROUP_CONCAT(?value; separator=', ') AS ?values) ?description ?date ?derivedFrom
WHERE {
    ?annotation oa:hasBody ?body ;
                oa:hasTarget ?target .
    VALUES ?body {
        <http://stad.gent/id/concepts/gent_words/310>
        <http://stad.gent/id/concepts/decision_making_themes/concept_46>
        <http://stad.gent/id/concepts/gent_words/169>
        <http://stad.gent/id/concepts/policy_domains_themes/concept_42>
        <http://stad.gent/id/concepts/policy_domains_themes/concept_47>
        <http://stad.gent/id/concepts/policy_domains_themes/concept_48>
        <http://stad.gent/id/concepts/decision_making_themes/concept_52>
    }
    ?target eli:title ?title ;
           besluit:motivering ?motivering ;
           prov:value ?value ;
           eli:description ?description ;
           eli:date_publication ?date ;
           prov:wasDerivedFrom ?derivedFrom .
    FILTER (CONTAINS(LCASE(?title), 'stof') || CONTAINS(LCASE(?description), 'stof') || CONTAINS(LCASE(?motivering), 'stof'))
}
ORDER BY DESC(?date)
LIMIT 3

