PREFIX besluit: <http://data.vlaanderen.be/ns/besluit#> 
PREFIX eli: <http://data.europa.eu/eli/ontology#> 
PREFIX prov: <http://www.w3.org/ns/prov#> 
SELECT ?title ?date ?motivation ?value 
WHERE { 
?entity eli:title ?title; 
eli:date_publication ?date; 
besluit:motivering ?motivation . 
OPTIONAL { ?besluit prov:value ?value. } 
filter contains(?title, "parkeerplaats voorbehouden voor elektrische voertuigen") 
} 
ORDER BY DESC(?date) 
LIMIT 10

PREFIX besluit: <http://data.vlaanderen.be/ns/besluit#> 
PREFIX oa: <http://www.w3.org/ns/oa#> 
PREFIX eli: <http://data.europa.eu/eli/ontology#> 
PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 
PREFIX prov: <http://www.w3.org/ns/prov#> 
SELECT ?title ?date ?motivation ?value 
WHERE { 
?entity eli:title ?title; 
eli:date_publication ?date; 
besluit:motivering ?motivation . 
OPTIONAL { ?entity prov:value ?value. } 
filter contains(?title, "straatverlichting") 
?annotation oa:hasTarget ?entity; 
oa:hasBody <http://stad.gent/id/concepts/gent_words/74> 
} 
ORDER BY DESC(?date)

PREFIX prov: <http://www.w3.org/ns/prov#> 
PREFIX besluit: <http://data.vlaanderen.be/ns/besluit#> 
PREFIX eli: <http://data.europa.eu/eli/ontology#> 
PREFIX oa: <http://www.w3.org/ns/oa#> 
SELECT ?title ?date ?motivation ?value 
{ 
?annotation oa:hasBody ?body ; 
oa:hasTarget ?target . 
VALUES ?body { <http://stad.gent/id/concepts/decision_making_themes/concept_21> <http://stad.gent/id/concepts/gent_words/82> } 
?target eli:title ?title ; 
eli:date_publication ?date; 
besluit:motivering ?motivation . 
OPTIONAL {?target prov:value ?value} 
} 
ORDER BY DESC(?date) 
LIMIT 10
